import "./Login.scss";

function Login() {
  const [loginUser, setLoginUser] = useState({
    email: "",
    password: "",
  });

  return (
    <div className="login">
      <div className="login__content">
        <form className="login__form" onSubmit={loginHandler}>
          <input
            className="input login__username"
            ref={inputReference}
            type="text"
            name="email"
            id="email"
            placeholder="email"
            onChange={(e) =>
              setLoginUser({ ...loginUser, email: e.target.value })
            }
          />
          <input
            className="input login__password"
            type="password"
            name="passowrd"
            id="password"
            placeholder="password"
            onChange={(e) =>
              setLoginUser({ ...loginUser, password: e.target.value })
            }
          />
          <button className="login__login-button">Login</button>
          <p className="login__signup" onClick={props.toggleSignUpModel}>
            SignUp
          </p>
        </form>
      </div>
    </div>
  );
}

export default LoginModel;
